import "./Expenses.css";
import React, { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import ExpenseItem from "./ExpenseItem";

const Expenses = (props) => {
  const [selectedOption, setSelectedOption] = useState("2020");

  const filterChangeHandler = (selectedYear) => {
    setSelectedOption(selectedYear);
  };

  const filteredYear = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === selectedOption;
  });

  let expensesContent = <p>No expenses found.</p>;

  if(filteredYear)

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          defaultYear={selectedOption}
          onSelect={filterChangeHandler}
        />
        {filteredYear.length === 0 && <p>No expenses found.</p>}
        {filteredYear.length > 0 && 
          filteredYear.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            />
          ))}
      </Card>
    </div>
  );
};

export default Expenses;
